import React, { useEffect, useState } from "react";

/**
 * Dashboard Component
 * Displays user profile data including savings goals, income sources, and expenses.
 */
function Dashboard() {
  const [profile, setProfile] = useState(null); // Holds profile data
  const [error, setError] = useState(null); // Holds error messages, if any

  // Fetch user profile data from the backend
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token"); // Retrieve token from localStorage

        // Fetch profile data from the server
        const response = await fetch("http://localhost:3001/dashboard", {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!response.ok) {
          throw new Error(`Error: ${response.status}`);
        }

        const data = await response.json();
        setProfile(data); // Set profile data
      } catch (err) {
        setError(err.message); // Set error message
      }
    };

    fetchProfile();
  }, []);

  // Render error message if an error occurs
  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  // Render loading indicator if profile data is not yet loaded
  if (!profile) {
    return <div className="loading">Loading...</div>;
  }

  // Render profile data
  return (
    <div className="dashboard">
      <h1>Dashboard</h1>

      <div className="profile-section">
        <h2>User ID: {profile.userId}</h2>

        {/* Savings Goals Section */}
        <div className="savings-goals">
          <h3>Savings Goals</h3>
          {profile.savingsGoals.length > 0 ? (
            <ul>
              {profile.savingsGoals.map((goal, index) => (
                <li key={index}>{JSON.stringify(goal)}</li>
              ))}
            </ul>
          ) : (
            <p>No savings goals found.</p>
          )}
        </div>

        {/* Income Sources Section */}
        <div className="income-sources">
          <h3>Income Sources</h3>
          {profile.incomeSources.length > 0 ? (
            <ul>
              {profile.incomeSources.map((source, index) => (
                <li key={index}>{JSON.stringify(source)}</li>
              ))}
            </ul>
          ) : (
            <p>No income sources found.</p>
          )}
        </div>

        {/* Expenses Section */}
        <div className="expenses">
          <h3>Expenses</h3>
          {profile.expenses.length > 0 ? (
            <ul>
              {profile.expenses.map((expense, index) => (
                <li key={index}>{JSON.stringify(expense)}</li>
              ))}
            </ul>
          ) : (
            <p>No expenses found.</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
